var Sequelize = require('sequelize');
var dotty = require('dotty');

module.exports = MsSqlModelInstance;

function MsSqlModelInstance(instance,model) {
	if(model===undefined){
		this.instance = instance;
	}
	else{
		this.instance = model.build(instance);
	}
}

MsSqlModelInstance.prototype.get = function(key){
	return this.instance.get(key);
}

MsSqlModelInstance.prototype.set = function(key,value){
	this.instance.set(key,value);
}

MsSqlModelInstance.prototype.save = function(cb){
	this.instance.save().then(				
			function(result){
				cb(null,'');
			},function(err){
				console.log('Error: ' + err);
			});	
}

MsSqlModelInstance.prototype.destroy = function(cb){
	this.instance.destroy().then(				
			function(result){
				cb(null,'');
			},function(err){
				console.log('Error: ' + err);
			});	
}

MsSqlModelInstance.prototype.getValues = function(cb){
	return this.instance.values;
}


