var Solr = require('solr-client');
var dotty = require('dotty');

module.exports = SolrInstance;

function SolrInstance(instance) {
		this.instance = instance;
}

SolrInstance.prototype.get = function(key){
	return this.instance.get(key);
}

SolrInstance.prototype.set = function(key,value){
	this.instance.set(key,value);
}

SolrInstance.prototype.save = function(cb){
	this.instance.save().then(				
			function(result){
				cb(null,'');
			},function(err){
				console.log('Error: ' + err);
			});	
}

SolrInstance.prototype.destroy = function(cb){
	this.instance.destroy().then(				
			function(result){
				cb(null,'');
			},function(err){
				console.log('Error: ' + err);
			});	
}

